import React, { useEffect } from 'react';
import './App.css';

interface Props {
    todos: Todo[];
    changeTodos: ChangeTodos;
}

export const Todos: React.FC<Props> = ({ todos, changeTodos }) => {

  async function GetTodoList() {
    const response = await fetch("http://localhost:8080/state/all", {method:'GET'});
    const dataBDD = await response.json();
    setTodoList(dataBDD);
  }

  async function ChangeFinishedStatus(dataFetch: Todo) {
    await fetch("http://localhost:8080/state/change",
    {
      method: 'PUT',
      body: JSON.stringify(dataFetch.id)
    });
  }

  useEffect(() => {GetTodoList()})
  return (
    <>
        {todos.map(todo => (
        <li>
            <label style={{ textDecoration: todo.complete ? 'line-through' : undefined }}>
                <input onChange={()=>{}} type="checkbox" checked={todo.complete} onClick={() => {changeTodos(todo);}}/> {todo.text} - {todo.id}
            </label>
        </li>
        ))}
    </>
  );
};
