import React, { useState, useEffect } from 'react';
import { Todos } from './Todos';
import { TodoInput } from './TodoInput';
import './App.css';

const initTodos: Todo[] = [
  {
    text: 'Promener le chien',
    complete: false,
  },
  {
    text: 'Aller chez le dentiste',
    complete: true,
  },
];

function App() {
  const [todos, setTodos] = useState(initTodos);

  //let response = fetch('http://localhost:8080/state/all')
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    let response = await (
      await fetch("https://jsonplaceholder.typicode.com/todos")
    ).json();
    setTodos(response);
  };

  const changeTodos: ChangeTodos = (todo: Todo) => {
    const newTodos = todos.map(unitTodo => {
      if (unitTodo === todo) {
        unitTodo.complete = !unitTodo.complete;
      }
      return (unitTodo);
    });
    setTodos(newTodos);
  };

  const addTodo: AddTodo = (text: string) => {
    const newTodo = { text, complete: false };
    setTodos([...todos, newTodo]);
  };

  return (
    <>
      <h1>Todos</h1>
      <ul className="mylist">
        <Todos todos = {todos} changeTodos = {changeTodos} />
      </ul>
      <TodoInput addTodo={addTodo} />
    </>
  );
}

export default App;
